.smmodal {
    .modal-content {
        max-width: 588px;
        width: 100%;
        border-radius: 20px;
        padding: 100px 40px 85px;
    }

    p {
        font-size: 20px;
        color: #181818;
        margin-bottom: 23px;

        img {
            vertical-align: middle;
            margin-top: -3px;
        }
    }

    button {
        margin-top: 50px;
    }
}

// -----------------
.mdmodal {
    .modal-content {
        max-width: 660px;
        width: 100%;
        border-radius: 20px;
        padding: 46px 68px;
    }

    .title {
        text-align: center;
        font-size: 20px;
        color: #181818;
        margin-bottom: 36px;
    }

    .bluebtn {
        margin-top: 45px;
    }
}

@media(max-width:1199.8px) {
    .bgmodal .modal-content {
        max-width: 900px;
    }
}

@media(max-width:991.8px) {
    .bgmodal .modal-content {
        max-width: 700px;
    }

    .inputfield.rpad {
        padding-left: 0;
    }

    #consultationmodal {
        .selwr {
            order: 1 !important;
        }

        .col-lg-7,
        .col-lg-5 {
            order: 2;
        }

        .col-lg-5.text-end {
            text-align: left !important;
        }

        .bluebtn {
            margin-top: 15px;
        }
    }

    .smmodal button {
        margin-top: 20px;
    }

    .smmodal .modal-content {
        padding: 80px 40px 60px;
    }
}

@media(max-width:767.8px) {
    .bgmodal .modal-content {
        max-width: 500px;
        padding: 50px 40px 40px 40px;
    }

    .modal .closemod {
        top: 26px;
        right: 26px;
    }

    .smmodal .modal-content {
        max-width: 500px;
    }

    .mdmodal .modal-content {
        max-width: 500px;
    }

}

@media(max-width:575.8px) {
    .bgmodal .title {
        margin-bottom: 20px;
        font-size: 18px;
    }

    .bgmodal .modal-content {
        max-width: calc(100vw - 20px);
        padding: 50px 20px 20px 20px;
    }

    .inputfield textarea {
        height: 140px;
    }

    .smmodal .modal-content {
        max-width: calc(100vw - 20px);
        padding: 55px 20px 40px;
    }

    .smmodal p {
        font-size: 18px;
        margin-bottom: 20px;
    }

    .smmodal button {
        margin-top: 10px;
    }

    .mdmodal .modal-content {
        max-width: calc(100vw - 20px);
        padding: 50px 20px 20px 20px;
    }

    .mdmodal .title {
        margin-bottom: 20px;
    }

    .inputfield.check label br {
        display: none;
    }

    .mdmodal .bluebtn {
        margin-top: 15px;
    }
}
